    #steering
    def steer_pos_init(self):
        self.steer_motion = bytearray([0x01])
        self.steer_motion_flag = 1


        vel = 0
        pos = 0
        steer_send_buffer = self.steer_motion + struct.pack('H',vel) + struct.pack('i',pos)

        # get rear_send_buffer
        send_buffer_1 = gol.get_value('send_buffer_1',self.hub_default_buffer)
        send_buffer_2 = gol.get_value('send_buffer_2',self.hub_default_buffer)
        steer_send_buffer_1 = gol.get_value('steer_send_buffer_1',self.steer_default_buffer)
        steer_send_buffer_2 = gol.get_value('steer_send_buffer_2',self.steer_default_buffer)

        steer_no = gol.get_value('steer_no')

        if steer_no == 0:
            steer_send_buffer_1 = steer_send_buffer
            steer_send_buffer_2 = steer_send_buffer
            gol.set_value('steer_motion_1',self.steer_motion)
            gol.set_value('steer_motion_flag_1',self.steer_motion_flag)

            gol.set_value('steer_motion_2',self.steer_motion)
            gol.set_value('steer_motion_flag_2',self.steer_motion_flag)

        elif steer_no ==1:
            steer_send_buffer_1 = steer_send_buffer
            steer_send_buffer_2 = bytearray([0x00]) + struct.pack('H',0) + struct.pack('i',0)

            gol.set_value('steer_motion_1',self.steer_motion)
            gol.set_value('steer_motion_flag_1',self.steer_motion_flag)
        elif steer_no == 2:
            steer_send_buffer_1 = bytearray([0x00]) + struct.pack('H',0) + struct.pack('i',0)
            steer_send_buffer_2 = steer_send_buffer

            gol.set_value('steer_motion_2',self.steer_motion)
            gol.set_value('steer_motion_flag_2',self.steer_motion_flag)
        
        Send_buffer = send_buffer_1 + send_buffer_2 + steer_send_buffer_1 + steer_send_buffer_2
        if self.ser.is_open:
            self.ser.write(Send_buffer)
            #print('send buffer',len(Send_buffer))


    def steer_pos_rel(self):
        self.steer_motion  = bytearray([0x02])
