        pos = int(self.lineEdit_12.text())

        steer_send_buffer = self.steer_motion + struct.pack('H',vel) + struct.pack('i',pos)

        # get rear_send_buffer
        send_buffer_1 = gol.get_value('send_buffer_1',self.hub_default_buffer)
        send_buffer_2 = gol.get_value('send_buffer_2',self.hub_default_buffer)
